mapper.insert:
insert into auth_info (id, access_token, token_type,
expires_in, refresh_token, re_expires_in,
r1_expires_in, r2_expires_in, w1_expires_in,
w2_expires_in, create_time, last_update_time
)
values (#{id,jdbcType=BIGINT}, #{accessToken,jdbcType=VARCHAR}, #{tokenType,jdbcType=VARCHAR},
#{expiresIn,jdbcType=BIGINT}, #{refreshToken,jdbcType=VARCHAR}, #{reExpiresIn,jdbcType=BIGINT},
#{r1ExpiresIn,jdbcType=BIGINT}, #{r2ExpiresIn,jdbcType=BIGINT}, #{w1ExpiresIn,jdbcType=BIGINT},
#{w2ExpiresIn,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}, #{lastUpdateTime,jdbcType=TIMESTAMP}
)
